INSERT INTO `tbl_question` (`id`, `code`, `questionImage`, `title`, `chapterId`, `option_1`, `option_2`, `option_3`, `option_4`, `trueAnswer`, `isDanger`) VALUES
('003e8387-fa00-4466-b066-31fa9d4adf06', 'Fiy7xqGT', ' ', '                                        Người ngồi trên xe mô tô hai bánh, xe mô tô ba bánh, xe gắn máy khi tham gia giao thông có được sử dụng ô khi trời mưa hay không?                                    ', 'abaa731e-c0cf-4bbc-99ca-ef2cd7b76ac1', ' 1. Được sử dụng.', ' 2. Chỉ người ngồi sau được sử dụng.', ' 3. Không được sử dụng.', ' 4. Được sử dụng nếu không có áo mưa.', 3, 0),
('00fe0ac0-af0b-499e-a91e-caf3d1aefc9c', 'mUqWFLJC', 'https://onthibanglaixe.net/img/q508.png', 'Xe nào đỗ vi phạm quy tắc giao thông?', 'c178f5c3-6950-4d1a-859b-7b366e8bfa49', '1. Cả hai xe.', '2. Không xe nào vi phạm.', '3. Chỉ xe mô tô vi phạm.', '4. Chỉ xe tải vi phạm.', 1, 0),
('026c776b-5598-410e-857b-cb5402f4e836', 'OBBKdfjN', '', 'Tốc độ tối đa cho phép đối với xe máy chuyên dùng, xe gắn máy (kể cả xe máy điện) và các loại xe tương tự trên đường bộ (trừ đường cao tốc) không được vượt quá bao nhiêu km/h?', 'abaa731e-c0cf-4bbc-99ca-ef2cd7b76ac1', '1. 50 km/h.', '2. 40 km/h.', '3. 60 km/h.', '', 2, 0),
('02e8c27a-dd15-44d6-be04-6ca7e6610c3c', 'nZ0iDPjW', '', 'Khi điều khiển xe trên đường vòng người lái xe cần phải làm gì để đảm bảo an toàn?', '4548014d-cf9c-4ab6-9b7c-7e7821b78562', '1. Quan sát cẩn thận các chướng ngại vật và báo hiệu bằng còi, đèn; giảm tốc độ tới mức cần thiết, về số thấp và thực hiện quay vòng với tốc độ phù hợp với bán kính cong của đường vòng.', '2. Quan sát cẩn thận các chướng ngại vật và báo hiệu bằng còi, đèn; tăng tốc để nhanh chóng qua đường vòng và giảm tốc độ sau khi qua đường vòng.', '', '', 1, 0),
('046bdbcd-6d27-45d2-bcfb-87082b40122c', '0lORMluB', 'https://onthibanglaixe.net/img/q319.png', 'Khi gặp biển nào xe được rẽ trái?', 'abaa731e-c0cf-4bbc-99ca-ef2cd7b76ac1', '1. Biển 1.', '2. Biển 2.', '3. Không biển nào.', '', 2, 0),
('048506b6-3e22-4827-8505-ec5c650da2c4', 'eOKkr394', '', 'Hành vi sử dụng xe mô tô để kéo, đẩy xe mô tô khác bị hết xăng đến trạm mua xăng có được phép hay không?', '50ccd9c5-ce5e-4921-9e23-de46a9558c38', '1. Chỉ được kéo nếu đã nhìn thấy trạm xăng.', '2. Chỉ được thực hiện trên đường vắng phương tiện cùng tham gia giao thông.', '3. Không được phép.', '', 3, 0),
('04fce361-67f9-4414-a7bd-05601da91038', 'P9vdOS1x', 'https://onthibanglaixe.net/img/q374.png', 'Biển nào báo hiệu sắp đến chỗ giao nhau nguy hiểm?', 'abaa731e-c0cf-4bbc-99ca-ef2cd7b76ac1', '1. Biển 1.', '2. Biển 1 và 2.', '3. Biển 2 và 3.', '4. Cả ba biển.', 4, 0),
('053d1957-ce6a-4b83-808d-f75bf47d567b', 'ipNqs0cy', '', 'Khi sử dụng giấy phép lái xe đã khai báo mất để điều khiển phương tiện cơ giới đường bộ, ngoài việc bị thu hồi giấy phép lái xe, chịu trách nhiệm trước pháp luật, người lái xe không được cấp giấy phép lái xe trong thời gian bao nhiêu năm?', 'abaa731e-c0cf-4bbc-99ca-ef2cd7b76ac1', '1. 02 năm.', '2. 03 năm.', '3. 05 năm.', '4. 04 năm.', 3, 0),
('060b7b99-3278-482c-b467-e0541a608658', 'Adl6Bbs8', 'https://onthibanglaixe.net/img/q480.png', 'Vạch kẻ đường nào dưới đây là vạch phân chia hai chiều xe chạy (vạch tim đường)?', 'abaa731e-c0cf-4bbc-99ca-ef2cd7b76ac1', '1. Vạch 1.', '2. Vạch 2.', '3. Vạch 3.', '4. Vạch 1 và 3.', 4, 0),
('07a74ffb-379b-4194-929b-fc333646551b', 'D2FedXeK', '', 'Tay ga trên xe mô tô hai bánh có tác dụng gì trong các trường hợp dưới đây?', 'abaa731e-c0cf-4bbc-99ca-ef2cd7b76ac1', '1. Để điều khiển xe chạy về phía trước.', '2. Để điều tiết công suất động cơ qua đó điều khiển tốc độ của xe.', '3. Để điều khiển xe chạy lùi.', '4. Cả ý 1 và ý 2.', 4, 0),
('099eaa39-add7-4caa-9c87-0b1197457871', 'KjJIvRxB', 'https://onthibanglaixe.net/img/q377.png', 'Biển nào báo hiệu nguy hiểm giao nhau với đường sắt?', 'abaa731e-c0cf-4bbc-99ca-ef2cd7b76ac1', '1. Biển 1 và 2.', '2. Biển 1 và 3.', '3. Biển 2 và 3.', '4. Cả ba biển.', 2, 0),
('0b9422d6-47a9-4413-9063-5c4582084269', 'P4bGs0c9', '', 'Bạn đang lái xe phía trước có một xe cứu thương đang phát tín hiệu ưu tiên bạn có được phép vượt hay không?', 'abaa731e-c0cf-4bbc-99ca-ef2cd7b76ac1', '1. Không được vượt.', '2. Được vượt khi đang đi trên cầu.', '3. Được phép vượt khi đi qua nơi giao nhau có ít phương tiện cùng tham gia giao thông.', '4. Được vượt khi đảm bảo an toàn.', 1, 0),
('0e09ba08-15ad-4f16-9b68-221e949e6fac', 'wPm6RX4s', '', 'Người lái xe được hiểu như thế nào trong các khái niệm dưới đây?', 'abaa731e-c0cf-4bbc-99ca-ef2cd7b76ac1', 'Là người điều khiển xe cơ giới', 'Là người điều khiển xe thô sơ.', 'Là người điều khiển xe có súc vật kéo.', '', 1, 0),
('0ebc4837-81c6-4d5d-80bd-c56bff690767', 'SjO802Q3', 'https://onthibanglaixe.net/img/q333.png', 'Khi gặp biển nào xe ưu tiên theo luật định vẫn phải dừng lại?', 'abaa731e-c0cf-4bbc-99ca-ef2cd7b76ac1', '1. Biển 1.', '2. Biển 2.', '3. Cả ba biển.', '', 2, 0),
('0fea7b09-1dba-4064-8805-884e2a25f590', 'h1BjFSeF', '', 'Để đảm bảo an toàn khi tham gia giao thông, người lái xe lái xe mô tô hai bánh cần điều khiển tay ga như thế nào trong các trường hợp dưới đây?', '4548014d-cf9c-4ab6-9b7c-7e7821b78562', '1. Tăng ga thật nhanh, giảm ga từ từ.', '2. Tăng ga thật nhanh, giảm ga thật nhanh.', '3. Tăng ga từ từ, giảm ga thật nhanh.', '4. Tăng ga từ từ, giảm ga từ từ.', 3, 0),
('10944c7f-ad06-473f-930a-6be9e02b9e78', 'wpzpNZRC', 'https://onthibanglaixe.net/img/q504.png', 'Thứ tự các xe đi như thế nào là đúng quy tắc giao thông?', 'c178f5c3-6950-4d1a-859b-7b366e8bfa49', '1. Xe khách, xe tải, mô tô, xe con.', '2. Xe con, xe khách, xe tải, mô tô.', '3. Mô tô, xe tải, xe khách, xe con.', '4. Mô tô, xe tải, xe con, xe khách.', 3, 0),
('11111f42-24f2-4428-97f7-7b5a9981d496', 'SJNISLUd', ' ', '                                         Người ngồi trên xe mô tô hai bánh, ba bánh, xe gắn máy khi tham gia giao thông có được mang, vác vật cồng kềnh hay không?                                    ', 'abaa731e-c0cf-4bbc-99ca-ef2cd7b76ac1', ' 1. Được mang, vác tùy trường hợp cụ thể.', ' 2. Không được mang, vác.', ' 3. Được mang, vác nhưng phải đảm bảo an toàn.', ' 4. Được mang vác tùy theo sức khỏe của bản thân.', 2, 0),
('114087db-1c48-4b95-81d6-687d11498e9b', 'TJ4Bzlc1', '', 'Tác dụng của mũ bảo hiểm đối với người ngồi trên xe mô tô hai bánh trong trường hợp xảy ra tai nạn giao thông là gì?', 'abaa731e-c0cf-4bbc-99ca-ef2cd7b76ac1', '1. Để làm đẹp.', '2. Để tránh mưa nắng', '3. Để giảm thiểu chấn thương vùng đầu.', '4. Để các loại phương tiện khác dễ quan sát.', 3, 0),
('143acae0-02a7-4f60-bd06-d3ce52ae8a1a', 'ubPYGiE9', '', 'Tại nơi đường giao nhau, khi đèn điều khiển giao thông có tín hiệu màu vàng, người điều khiển phương tiện giao thông phải chấp hành như thế nào là đúng quy tắc giao thông?', 'abaa731e-c0cf-4bbc-99ca-ef2cd7b76ac1', '1. Phải cho xe dừng lại trước vạch dừng, trường hợp đã đi quá vạch dừng hoặc đã quá gần vạch dừng nếu dừng lại thấy nguy hiểm thì được đi tiếp.', '2. Trong trường hợp tín hiệu vàng nhấp nháy là được đi nhưng phải giảm tốc độ, chú ý quan sát nhường đường cho người đi bộ qua đường.', '3. Nhanh chóng tăng tốc độ, vượt qua nút giao và chú ý đảm bảo an toàn.', '4. Cả ý 1 và ý 2.', 4, 0),
('163a1cd1-3e1d-4674-bca4-434868e9cf0f', 'F2Rl3CpM', ' ', '                                         Người điều khiển xe mô tô hai bánh, ba bánh, xe gắn máy có được phép sử dụng xe để kéo hoặc đẩy các phương tiện khác khi tham gia giao thông không?                                    ', 'abaa731e-c0cf-4bbc-99ca-ef2cd7b76ac1', ' 1. Được phép.', ' 2. Nếu phương tiện được kéo, đẩy có khối lượng nhỏ hơn phương tiện của mình.', ' 3. Tùy trường hợp.', ' 4. Không được phép.', 4, 0),
('16bc047f-0e3a-461c-a7d0-25936c907cae', 'pkWHcfSO', '', 'Khi xảy ra tai nạn giao thông, có người bị thương nghiêm trọng, người lái xe và người có mặt tại hiện trường vụ tai nạn phải thực hiện các công việc gì?', 'abaa731e-c0cf-4bbc-99ca-ef2cd7b76ac1', '1. Thực hiện sơ cứu ban đầu trong trường hợp khẩn cấp; thông báo vụ tai nạn đến cơ quan thi hành pháp luật.', '2. Nhanh chóng lái xe gây tai nạn hoặc đi nhờ xe khác ra khỏi hiện trường vụ tai nạn.', '3. Cả ý 1 và ý 2.', '', 1, 0),
('1880f240-9941-4878-a8b6-09e6d1c07f54', 'AofQ0dyl', '', 'Hành vi vận chuyển đồ vật cồng kềnh bằng xe mô tô, xe gắn máy khi tham gia giao thông có được phép hay không?', '50ccd9c5-ce5e-4921-9e23-de46a9558c38', '1. Không được vận chuyển.', '2. Chỉ được vận chuyển khi đã chằng buộc cẩn thận.', '3. Chỉ được vận chuyển vật cồng kềnh trên xe máy nếu khoảng cách về nhà ngắn hơn 2 km.', '', 1, 0),
('197fcbde-f540-4092-9683-2880688f0879', 'RBKK8G1u', 'https://onthibanglaixe.net/img/q596.png', 'Xe của bạn đang di chuyển gần đến khu vực giao cắt với đường sắt, khi rào chắn đang dịch chuyển, bạn điều khiển xe như thế nào là đúng quy tắc giao thông?', 'abaa731e-c0cf-4bbc-99ca-ef2cd7b76ac1', '1. Quan sát nếu thấy không có tàu thì tăng tốc cho xe vượt qua đường sắt.', '2. Dừng lại trước rào chắn một khoảng cách an toàn.', '3. Ra tín hiệu, yêu cầu người gác chắn tàu kéo chậm Barie để xe bạn qua.', '', 2, 0),
('1a25195b-d825-4065-88b3-f890295fcc80', '13PqvpGL', '  ', '                                                                                 Khi đang lái xe mô tô và ô tô, nếu có nhu cầu sử dụng điện thoại để nhắn tin hoặc gọi điện, người lái xe phải thực hiện như thế nào trong các tình huống nêu dưới đây?                                                                        ', 'abaa731e-c0cf-4bbc-99ca-ef2cd7b76ac1', '  1. Giảm tốc độ để đảm bảo an toàn với xe phía trước và sử dụng điện thoại để liên lạc.', '  2. Giảm tốc độ để dừng xe ở nơi cho phép dừng xe sau đó sử dụng điện thoại để liên lạc.', '  3. Tăng tốc độ để cách xa xe phía sau và sử dụng điện thoại để liên lạc.', '  ', 2, 0),
('1a51f509-4df3-402e-ac73-48206c69886d', 'spEDjpTS', 'https://onthibanglaixe.net/img/q495.png', 'Xe nào được quyền đi trước trong trường hợp này?', 'abaa731e-c0cf-4bbc-99ca-ef2cd7b76ac1', '1. Mô tô.', '2. Xe cứu thương.', '', '', 2, 0),
('1b51c0a0-7067-4f66-994b-dc1ba352659b', 'Mz8pEqhu', 'https://onthibanglaixe.net/img/q311.png', 'Khi gặp biển nào thì xe mô tô hai bánh được đi vào?', 'abaa731e-c0cf-4bbc-99ca-ef2cd7b76ac1', '1. Không biển nào.', '2. Biển 1 và 2.', '3. Biển 2 và 3.', '4. Cả ba biển.', 3, 0),
('1b5c9caa-202c-4e63-a1d7-d057d6d75629', 'm28cLekW', 'https://onthibanglaixe.net/img/q528.png', 'Các xe đi như thế nào là đúng quy tắc giao thông?', 'abaa731e-c0cf-4bbc-99ca-ef2cd7b76ac1', '1. Các xe ở phía tay phải và tay trái của người điều khiển được phép đi thẳng.', '2. Cho phép các xe ở mọi hướng được rẽ phải.', '3. Tất cả các xe phải dừng lại trước ngã tư, trừ những xe đã ở trong ngã tư được phép tiếp tục đi.', '', 3, 0),
('1badd97a-161c-48f6-a418-15d039b786dc', 'okVJxJST', 'https://onthibanglaixe.net/img/q562.png', 'Các xe đi theo hướng mũi tên, xe nào vi phạm quy tắc giao thông?', 'abaa731e-c0cf-4bbc-99ca-ef2cd7b76ac1', '1. Xe con, xe tải, xe khách.', '2. Xe tải, xe khách, xe mô tô.', '3. Xe khách, xe mô tô, xe con.', '4. Cả bốn xe.', 2, 0),
('211b469b-fdd4-452b-9466-ba4ec873a620', 'syBAO5dk', 'https://onthibanglaixe.net/img/q310.png', 'Biển nào báo hiệu cấm xe mô tô hai bánh đi vào?', 'abaa731e-c0cf-4bbc-99ca-ef2cd7b76ac1', '1. Biển 1.', '2. Biển 2.', '3. Biển 3.', '', 1, 0),
('22087caa-2d2e-480e-aefd-e5fea6a32d44', '8v3n9Mhd', 'https://onthibanglaixe.net/img/q72.png', ' Biển báo hiệu có dạng hình tròn, viền đỏ, nền trắng, trên nền có hình vẽ hoặc chữ số, chữ viết màu đen là loại biển gì dưới đây?', 'abaa731e-c0cf-4bbc-99ca-ef2cd7b76ac1', '1. Biển báo nguy hiểm.', '2. Biển báo cấm.', '3. Biển báo hiệu lệnh.', '4. Biển báo chỉ dẫn.', 2, 0),
